import { LOAD_POSTS, VOTE_ON_POST } from '../actions/Posts'

export function postsReducer(state={},action){
	switch(action.type){
		case LOAD_POSTS:
			const { posts } = action
			return {
				...state,
				posts
			}
		case VOTE_ON_POST:
			var aux = state
			console.log(action.voteScore)
			aux.posts[action.id].voteScore = action.voteScore
			return {
				aux
			}		
		default:
			return state

	}
}

export default postsReducer
