import { LOAD_POSTS, VOTE_ON_POST } from '../actions/Posts'

export function postsReducer(state={},action){
	switch(action.type){
		case LOAD_POSTS:
			const { posts } = action
			return {
				...state,
				posts
			}
		case VOTE_ON_POST:
			const currentPostUpVote = [...state.posts]
			console.log(currentPostUpVote)
		      const indexUp= currentPostUpVote.findIndex(post => post.id === action.postId)
		      currentPostUpVote[indexUp].voteScore = currentPostUpVote[indexUp].voteScore + 1
		      return {
		        posts: [...currentPostUpVote]
		      }
		default:
			return state

	}
}

export default postsReducer
