import * as ReadableAPI from '../utils/ReadableAPI'

export const LOAD_COMMENTS = 'LOAD_COMMENTS'

export const loadComments = comments => ({
	type: LOAD_COMMENTS,
	comments
})

export const fetchComments = postId => dispatch => (
	ReadableAPI
	.getComments(postId)
	.then(comments => dispatch(loadComments(comments)))
);



export const fetchRegisterComment = (body,author,postId,id=uuid(),timestamp=Date.now()) => dispatch => (
	ReadableAPI
	.registerComment(body,author,postId,id,timestamp)
);

export const vote = (commentId,type) => dispatch => {
	ReadableAPI.voteComment(commentId,type).then((post) => {
		dispatch({
		type: VOTE_ON_COMMENT,
		comment
		})
	})
};

export const deleteComment = (commentId) => dispatch => {
	ReadableAPI.deleteComment(commentId).then((comment) => {
		dispatch({
		type: DELETE_COMMENT,
		commentId
		})
	})
};
