  }
  render (){
    const {categories} = this.state
    console.log(categories)
    //if(categories.length === 0)
     // return(<h1>Deu merda</h1>)
    return(
      <div>
        <ol>
     <SimpleMenu categories/>
      </ol>
     </div>
    )
  }
}
