import { LOAD_POSTS, VOTE_ON_POST } from '../actions/Posts'

export function postsReducer(state={},action){
	switch(action.type){
		case LOAD_POSTS:
			const { posts } = action
			return {
				...state,
				posts
			}
		case VOTE_ON_POST:
			var aux = state
			console.log(aux)
			var i;
			var j;
			for(i=0;i<aux.posts.length;i++){
				if(aux.posts[i].id === action.id){
					j = i;
					i = aux.posts.length;
				}
			}
			console.log(j)
			if(j !== undefined)
				aux.posts[j].voteScore = action.voteScore
			return {
				aux
			}		
		default:
			return state

	}
}

export default postsReducer
