import React, { Component } from 'react';
import {connect} from 'react-redux'

// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import Button from "@material-ui/core/Button";

import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

import { getCommentDetails,editComment } from '../actions/comments';

const styles = theme => ({
  button: {
    marginTop: theme.spacing.unit * 3,
    marginLeft: theme.spacing.unit,
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  }
});
class EditComment extends Component{

  componentDidMount(){
    this.props.dispatch(getcommentDetails(this.props.match.params.commentId)).then(res =>{
      this.setState({comment: res, commentTitle:res.title, commentContent:res.body})
    });
  }

  state = {
    commentTitle:'',
    commentContent:'',
    comment: '',
  }

  handleChangeContent = event => {
    this.setState({ commentContent: event.target.value });
  };
  handleChangeTitle = event => {
    this.setState({ commentTitle: event.target.value });
  };
  handleEdit = (commentTitle,commentContent,commentId) => {
    this.props.dispatch(editcomment(commentContent,commentId))
  };

  render (){
    var {commentTitle,commentContent,comment} = this.state;
     return(<div>
      <React.Fragment>
      <Typography variant="h6" gutterBottom>
        Edit comment
      </Typography>
      <Grid container spacing={24}>
        <Grid item xs={12} sm={6}>
          <TextField
            disabled
            id="name"
            name="name"
            label={comment.author}
            editable='false'
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="title"
            name="title"
            value={commentTitle}
            label="comment Title"
            onChange={this.handleChangeTitle}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            required
            id="body"
            name="body"
            value={commentContent}
            label="comment Body"
            onChange={this.handleChangeContent}
            fullWidth
          />
          <br/>
        <Grid item xs={12} sm={12}>
          <Button 
            size="small" 
            color="primary" 
            fullWidth 
            variant="contained"
            onClick={() => this.handleEdit(commentTitle,commentContent,comment.id)}>
            Edit
          </Button>
        </Grid>
        </Grid>
      </Grid>
    </React.Fragment>
    <br/>
      </div>
    )
  }
}

export default connect ()(withStyles(styles)(EditComment))
