import { LOAD_COMMENTS } from '../actions/Comments'

export function commentsReducer(state={},action){
	switch(action.type){
		case LOAD_COMMENTS:
			const { comments } = action
			return {
				...state,
				comments
			}
		default:
			return state
	}
}

export default commentsReducer
