import { LOAD_POSTS, VOTE_ON_POST } from '../actions/Posts'

export function postsReducer(state={},action){
	switch(action.type){
		case LOAD_POSTS:
			const { posts } = action
			return {
				...state,
				posts
			}
		case VOTE_ON_POST:
			return {
				...state,
				posts: { ... state.posts, voteScore: action.voteScore}
			}		
		default:
			return state

	}
}

export default postsReducer
