import React, { Component } from 'react';
import {connect} from 'react-redux'

// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import InputLabel from "@material-ui/core/InputLabel";
import Button from "@material-ui/core/Button";

import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import { fetchRegisterPost } from '../actions/Posts';
import { fetchCategories } from '../actions/Category';

const styles = theme => ({
  button: {
    marginTop: theme.spacing.unit * 3,
    marginLeft: theme.spacing.unit,
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  }
});
class RegisterPost extends Component{

  componentDidMount(){
    if(this.props.categories === undefined)
      this.props.dispatch(fetchCategories());
  }

  state = {
    posts:[],
    category:'',
    name:'',
    postTitle:'',
    postContent:'',
    error:'',
  }

  handleRegister = (title,body,author,category) => () =>{
    var error = ''
    if(title === '')
      error = 'title';
    if(body === '')
      error = 'content';
    if(author === '')
      error = 'name';
    if(category === '')
      error = 'category';
    if(error !== ''){
      this.props.dispatch(fetchRegisterPost(title,body,author,category))
      this.setState({modalOk:true})
    }
    else
      this.setState({modalError:true,error:error})
  };
  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render (){
    var categories = this.props.categories;
    var itens = ''
    if(typeof categories !== 'undefined')
    itens = categories.map((category) => (
      <option key={category.name} value={category.name}>{category.name}</option>
    ))
    else{
      this.props.dispatch(fetchCategories);
    }
     return(<div>
      <React.Fragment>
      <Typography variant="h6" gutterBottom>
        New Post
      </Typography>
      <Grid container spacing={24}>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="name"
            name="name"
            label="Name"
            onChange={this.handleChange('name')}
            fullWidth
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="title"
            name="title"
            label="Post Title"
            onChange={this.handleChange('postTitle')}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            required
            id="body"
            name="body"
            label="Post Content"
            onChange={this.handleChange('postContent')}
            fullWidth
          />
        </Grid>
        <Grid item xs={12}>
          <FormControl required>
          <InputLabel >Category</InputLabel>
          <Select
            native
            value={this.state.category}
            name="category"
            onChange={this.handleChange('category')}
            inputProps={{
              id: 'age-native-required',
            }}
          >
            <option value="" />
            {itens
            }
          </Select>
        </FormControl>
        </Grid>

      </Grid>
    </React.Fragment>
    <br/>
      <Button onClick={this.handleRegister('a','b','c','react')}>
        Post
      </Button>
      </div>
    )
  }
}
function mapStateToProps (state) {
    return {  
      categories:state.categoryReducer.categories,
    }
  }

export default connect (mapStateToProps)(withStyles(styles)(RegisterPost))
