import { LOAD_POSTS, VOTE_ON_POST } from '../actions/Posts'

export function postsReducer(state={},action){
	switch(action.type){
		case LOAD_POSTS:
			const { posts } = action
			return {
				...state,
				posts
			}
		case VOTE_ON_POST:
			const currentPostUpVote = [...state.posts]
	      	const index= currentPostUpVote.findIndex(post => post.id === action.post.id)
    		currentPostUpVote[index].voteScore = action.voteScore
    		console.log([...currentPostUpVote])
		    return {
		      posts: [...currentPostUpVote]
		    }
		default:
			return state

	}
}

export default postsReducer
