import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import React, { Component } from 'react';
import {connect} from 'react-redux'

import ModalComments from './ModalComments'
import { vote } from '../actions/Posts'
const styles = {
  media: {
    // ⚠️ object-fit is not supported by IE 11.
    objectFit: 'cover',
  },
};

class ImgMediaCard extends Component{
    state = {
        modalOpen: false
    }
    handleClick = () =>{
        this.setState({modalOpen:true});
    }
    handleClose = () =>{
        this.setState({modalOpen: false});
    }
    handleVote(postId,type){
        vote(postId,type)
    }
    render(){

  const { classes,header,body, postId, votes } = this.props;
  return (
            <Card className={classes.card} >
            <CardActionArea onClick={this.handleClick}>
                <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                    {header}
                </Typography>
                <Typography component="p">
                    {body}
                </Typography>
                <Typography component="p">
                    {`Votes: ${votes}`} 
                </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button size="small" color="primary" onClick={this.props.dispatch(vote(postId,'upVote'))}>
                Vote Up
                </Button>
                <Button size="small" color="primary" onClick={() => vote(postId,'downVote')}>
                Vote Down
                </Button>
                <ModalComments handleClose={this.handleClose} open={this.state.modalOpen} postId={postId}></ModalComments>
            </CardActions>
            </Card>
  );
}
}

ImgMediaCard.propTypes = {
  classes: PropTypes.object.isRequired,
  header: PropTypes.string.isRequired,
  body: PropTypes.string.isRequired,
};

export default connect()(withStyles(styles)(ImgMediaCard));
