import { LOAD_COMMENTS } from '../actions/Comments'

export function commentsReducer(state={},action){
	switch(action.type){
		case LOAD_COMMENTS:
			const { comments } = action
			return {
				...state,
				comments
			}
		case VOTE_ON_COMMENT:
			const currentCommentVote = [...state.comments]
	      	const index= currentCommentVote.findIndex(comment => comment.id === action.comment.id)
    		currentCommentVote[index].voteScore = action.comment.voteScore
		    return {
		      posts: [...currentCommentVote]
		    }
		 case DELETE_POST:
		 	var currentPostDelete = [...state.posts]
	      	const indexDelete = currentPostDelete.findIndex(post => post.id === action)
	      	var aux = currentPostDelete.splice(indexDelete,1)
	      	return {
		      posts: [...currentPostDelete]
		    }
		default:
			return state
	}
}

export default commentsReducer
